import React,{useEffect,useState} from 'react';
import ListItems from './ListItems';
import { connect } from "react-redux";
import {dispatchGetAllBuckets,dispatchBucket,dispatchDeleteBucket,dispatchGetTodoList,dispatchToDoList} from "../Redux/Actions/Actions";
import {useParams} from "react-router-dom";


const HeadingWithList = ({listItems,dispatchGetAllBuckets,typePlaceholder,type,dispatchBucket,dispatchDeleteBucket}) => {
    const [inputValue,setInputValue] = useState("");
    let { id } = useParams();
    useEffect(()=>{
        if(id) dispatchGetTodoList(id);
        else dispatchGetAllBuckets();
    },[id])
    const handelOnKeyDown = (e) => {
        if (e.which=="13"){
            if(inputValue.trim()==='') alert("can't create empty bucket")
            else  {
                setInputValue("");
                if(id) dispatchToDoList(inputValue,listItems);
                dispatchBucket(inputValue,listItems)
            }
        }
    }
    const handleChange = (e)=>{
        setInputValue(e.target.value);
    }
    const handleClick = (e,id) => {
        dispatchDeleteBucket(id);
    }

    return (
    <div className="container">
        <h1>{type}</h1>
        <input placeholder={typePlaceholder} type="text" onKeyDown={handelOnKeyDown} onChange={handleChange} value={inputValue}/>
        {listItems && <ListItems listItems={listItems} listType="link" handleAction={handleClick}/>}
    </div>
    )
}


function mapStateToProps(state) {
    return {
        listItems:state.allBucketList
    };
  }

  const mapDispatchToProps = {
    dispatchGetAllBuckets,
    dispatchBucket,
    dispatchDeleteBucket,
    dispatchGetTodoList,
    dispatchToDoList

  };

  export default connect(mapStateToProps, mapDispatchToProps)(HeadingWithList);
