import React,{useState} from "react";
import { Link } from "react-router-dom";
import "./listItem.css"

export default function ListItems({ listItems, listType, handleAction,updateTodo,handleCheckbox }) {
  const [editBox,setEditBox]=useState('');
  const handleChange = (e)=>{
    setEditBox({
      id:editBox.id,
      bucketID:editBox.bucketID,
      value:e.target.value
    })
  }

  const handelKeyDown = (e)=>{
    if (e.which === "13") {
      if (editBox.value.trim() === "") alert("can't create empty todo");
      else {
        console.log(editBox);
        updateTodo(editBox.id,editBox.value,editBox.bucketID);
        setEditBox("");
      }
    }
  }
  const bucketList = () => {
    let h = [];
    listItems.map((item, index) => {
      h.push(
        listType ? (
          <div className="bucket-list" key={index}>
            <Link to={`/${item.title}_${item.bucketID}`}  className="col-sm-9 col-xs-9">
              <li>{item.title}</li>
            </Link>
                <div className="col-sm-3 col-xs-3">                <i className="fa fa-trash " onClick={(e) => handleAction(e, "deleteBucket", item.bucketID)}></i></div>

           </div>
        ) : (
          <li key={index} className={`${item.markDone ? 'crossed' : 'row'}`}>
            <input classname="col-sm-2" type="checkbox" defaultChecked={item.markDone} onChange={()=>{handleCheckbox(item.todoID,item.bucketID)}}/> 
            <label classname="col-sm-6">{item.title}</label>
            <button classname="col-sm-2" onClick={(e) => handleAction(e, "deleteTodo",item.bucketID,item.todoID)}>D</button>{" "}
            <button classname="col-sm-2"onClick={() => setEditBox({id:item.todoID,value:item.title,bucketID:item.bucketID})}>E</button>
            {editBox && editBox.id===item.todoID && <input value={editBox.value} onChange={handleChange} onKeyDown={handelKeyDown}/>}
          </li>
        )
      );
      return null;
    });
    return h;
  };
  return  <ul className="list-unstyled">{bucketList()}</ul> ;
}
