import React,{useState} from "react";
import { Link } from "react-router-dom";

export default function ListItems({ listItems, listType, handleAction,handleChange,handelKeyDown }) {
  const [editBox,setEditBox]=useState('');
  const bucketList = () => {
    let h = [];
    listItems.map((item, index) => {
      h.push(
        listType ? (
          <Link to={`/${item.title}_${item.bucketID}`} key={index}>
            <li>{item.title}</li>
            <button
              onClick={(e) => handleAction(e, "deleteBucket", item.bucketID)}
            >
              D
            </button>
          </Link>
        ) : (
          <li key={index}>
            <input type="checkbox" /> {item.title}{" "}
            <button onClick={(e) => handleAction(e, "deleteTodo",item.bucketID,item.todoID)}>D</button>{" "}
            <button onClick={() => setEditBox({id:item.todoID,value:item.title})}>E</button>
            {editBox.id==item.todoID && <input value={editBox.value} onChange={handleChange} onKeyDown={handelKeyDown}/>}
          </li>
        )
      );
    });
    return h;
  };
  return listItems ? <ul className="list-unstyled">{bucketList()}</ul> : null;
}
