import React, { useEffect, useState } from "react";
import ListItems from "./ListItems";
import { connect } from "react-redux";
import {
  dispatchGetAllBuckets,
  dispatchBucket,
  dispatchDeleteBucket,
  dispatchGetTodoList,
  dispatchToDoList,
  allBucketList
} from "../Redux/Actions/Actions";
import { useParams } from "react-router-dom";

const HeadingWithList = ({
  listItems,
  dispatchGetAllBuckets,
  typePlaceholder,
  type,
  dispatchBucket,
  dispatchDeleteBucket,
  dispatchGetTodoList,
  dispatchToDoList,
  allBucketList
}) => {
  const [inputValue, setInputValue] = useState("");
  let { id } = useParams();
  useEffect(() => {
    allBucketList([]);
    if (id) {
      id=id.split('_');
      dispatchGetTodoList(id[1]);
    }
    else dispatchGetAllBuckets();
  }, [id, dispatchGetAllBuckets, dispatchGetTodoList]);
  const handelOnKeyDown = (e) => {
    if (e.which == "13") {
      if (inputValue.trim() === "") alert("can't create empty bucket");
      else {
        setInputValue("");
        if (id) {
          dispatchToDoList(inputValue, id, listItems);
        } else {
          dispatchBucket(inputValue, listItems);
        }
      }
    }
  };
  const handleChange = (e) => {
    setInputValue(e.target.value);
  };
  const handleAction = (e, type,id) => {
    switch(type) {
      case "deleteBucket":
        dispatchDeleteBucket(id);
        break;
      case "deleteTodo":
        dispatchDeleteTodo(id);
        break;

    }

  };

  return (
    <div className="container">
      <h1>{type}</h1>
      <input
        placeholder={typePlaceholder}
        type="text"
        onKeyDown={handelOnKeyDown}
        onChange={handleChange}
        value={inputValue}
      />
      {listItems && listItems.length>0 && (
        <ListItems
          listItems={listItems}
          listType={id?"":"link" }
          handleAction={handleAction}
        />
      )}
    </div>
  );
};

function mapStateToProps(state) {
  return {
    listItems: state.allBucketList,
  };
}

const mapDispatchToProps = {
  dispatchGetAllBuckets,
  dispatchBucket,
  dispatchDeleteBucket,
  dispatchGetTodoList,
  dispatchToDoList,
  allBucketList
};

export default connect(mapStateToProps, mapDispatchToProps)(HeadingWithList);
